        error as Error
      );
    }
  }

  /**
   * Genera una respuesta simulada para prop√≥sitos de desarrollo
   */
  private generateSimulatedResponse(query: AIQuery): Promise<AIResponse> {
    this.logger.debug('Generating simulated response for query');

    // Simular un peque√±o retraso para emular el procesamiento
    return new Promise<AIResponse>((resolve) => {
      setTimeout(() => {
        if (query.context?.type === 'emr' && query.unstructuredNotes) {
          resolve({
            answer: `Basado en el an√°lisis de ${query.unstructuredNotes.length} notas m√©dicas,
                        he organizado la informaci√≥n y detectado varios puntos importantes...`,
            confidence: 0.89,
            timeline: [
              {
                date: '2023-01-15',
                events: [
                  {
                    type: 'consultation',
                    description: 'Consulta inicial por dolor de rodilla',
                    source: 'Nota cl√≠nica',
                    confidence: 0.95,
                  },
                ],
              },
              {
                date: '2023-03-10',
                events: [
                  {
