   */
  private loadSavedConfig(): void {
    try {
      // En una aplicaci√≥n real, podr√≠amos cargar esto desde una API o base de datos

      if (savedAdapterName && savedConfig) {
        this.logger.info(
          `Cargando configuraci√≥n guardada para: ${savedAdapterName}`
        );

        // Obtener adaptador con la configuraci√≥n guardada
        this.currentAdapter = EMRAdapterFactory.getAdapter(
          savedAdapterName,
          config
        );
        this.currentAdapterName = savedAdapterName;
        this.currentConfig = config;
      } else {
        this.logger.info('No hay configuraci√≥n guardada');
      }
    } catch (error) {
      this.logger.error('Error al cargar configuraci√≥n guardada', { error });
    }
  }

  /**
   * Guarda la configuraci√≥n actual
   */
  private saveConfig(): void {
    if (this.currentAdapterName && this.currentConfig) {
      localStorage.setItem('emrAdapterName', this.currentAdapterName);
      localStorage.setItem(
        'emrAdapterConfig',
        JSON.stringify(this.currentConfig)
      );
      this.logger.info(
        `Configuraci√≥n guardada para: ${this.currentAdapterName}`
      );
    }
  }

  /**
   * Resetea la configuraci√≥n actual
   */
  public resetConfig(): void {
    localStorage.removeItem('emrAdapterName');
    localStorage.removeItem('emrAdapterConfig');
    this.currentAdapter = null;
    this.currentAdapterName = null;
    this.currentConfig = null;
    this.logger.info('Configuraci√≥n reseteada');
  }

  /**
   * Verifica si hay un adaptador configurado
   */
  public hasConfiguredAdapter(): boolean {
    return (
      this.currentAdapter !== null && this.currentAdapterName !== 'GENERIC'
    );
  }
}

