  /**
   * Obtiene un token de acceso para ClinicCloud
   */
  private async getAccessToken(): Promise<string> {
    try {
      // Si tenemos un token v√°lido, lo devolvemos
      if (
        this.accessToken &&
        this.tokenExpiration &&
        new Date() < this.tokenExpiration
      ) {
        return this.accessToken;
      }

      // Si no, debemos obtener uno nuevo
      let token;

      // ClinicCloud soporta dos m√©todos de autenticaci√≥n:
      // 1. API Key simple
      // 2. OAuth2 con flujo de client credentials
      if (this.clientId && this.clientSecret) {
        // Usar OAuth2
        const response = await fetch(`${this.apiUrl}/oauth/token`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
            'X-Clinic-Id': this.clinicId,
          },
          body: new URLSearchParams({
            grant_type: 'client_credentials',
            client_id: this.clientId,
            client_secret: this.clientSecret,
            scope: 'read write',
          }),
        });

        if (!response.ok) {
          throw new Error(
            `Error en autenticaci√≥n OAuth2: ${response.status} ${response.statusText}`
          );
        }


        token = data.access_token;

        // Guardar token y calcular expiraci√≥n (t√≠picamente 1 hora)
        this.accessToken = token;
        this.tokenExpiration = new Date(Date.now() + data.expires_in * 1000);
      } else {
        // Usar API Key simple
        token = this.apiKey;

        // En este caso, no hay expiraci√≥n (el API key es de larga duraci√≥n)
        this.accessToken = token;
        this.tokenExpiration = new Date(Date.now() + 365 * 24 * 60 * 60 * 1000); // 1 a√±o
      }

      this.logger.info('Token de acceso ClinicCloud obtenido exitosamente');
      return token;
    } catch (error) {
      this.logger.error('Error al obtener token de acceso ClinicCloud', {
        error,
      });
      throw new Error(
        `Error al obtener token de acceso: ${(error as Error).message}`
      );
    }
  }

