  /**
   * Mapea el tipo de tratamiento del formato de la aplicaci√≥n al de ClinicCloud
   */
  private reverseMapTreatmentType(type: string): string {
    switch (type) {
      case 'medication':
        return 'medicamento';
      case 'procedure':
        return 'procedimiento';
      case 'therapy':
        return 'terapia';
      case 'lifestyle':
        return 'estilo de vida';
      default:
        return 'otro';
    }
  }

  /**
   * Mapea el estado del tratamiento del formato de la aplicaci√≥n al de ClinicCloud
   */
  private reverseMapTreatmentStatus(status: string): string {
    switch (status) {
      case 'active':
        return 'activo';
      case 'completed':
        return 'completado';
      case 'cancelled':
        return 'cancelado';
      case 'scheduled':
        return 'programado';
      default:
        return 'activo';
    }
  }

  /**
   * Procesa m√©tricas del formato de ClinicCloud al formato de la aplicaci√≥n
   */
  private processMetricas(
    metricas: ClinicCloudMetricaResult,
    metrics: EMRPatientMetrics
  ): void {
    if (!metricas?.metricas) return;

    metricas.metricas.forEach((metrica: ClinicCloudMetrica) => {


      switch (metrica.tipo.toLowerCase()) {
        case 'peso':
          metrics.weight.push({
            date: fecha,
            value: metrica.valor,
            units: metrica.unidad || 'kg',
          });
          break;
        case 'altura':
          metrics.height.push({
            date: fecha,
            value: metrica.valor,
            units: metrica.unidad || 'cm',
          });
          break;
        case 'tension':
          if (metrica.sistolica && metrica.diastolica) {
            metrics.bloodPressure.push({
              date: fecha,
              systolic: metrica.sistolica,
              diastolic: metrica.diastolica,
              units: metrica.unidad || 'mmHg',
            });
          }
          break;
        case 'glucosa':
          metrics.glucose.push({
            date: fecha,
            value: metrica.valor,
            units: metrica.unidad || 'mg/dL',
          });
          break;
        case 'colesterol':
          metrics.cholesterol.push({
            date: fecha,
            value: metrica.valor,
            units: metrica.unidad || 'mg/dL',
          });
          break;
      }
    });
  }
