/**
 * Tipos espec√≠ficos para EPIC usando FHIR
 */
// Tipo base para recursos FHIR
interface FHIRResource {
  resourceType: string;
  id?: string;
  meta?: {
    versionId?: string;
    lastUpdated?: string;
  };
}

// Definir interfaz para EMRLabResult con la propiedad requerida orderedBy
interface EMRLabResult {
  id: string;
  patientId: string;
  date: Date;
  type: string;
  name: string;
  results: Record<
    string,
    {
      value: string | number;
      unit?: string;
      referenceRange?: string;
      isAbnormal?: boolean;
    }
  >;
  units?: string;
  range?: string;
  abnormal?: boolean;
  notes?: string;
  orderedBy: string;
}

// Interfaces adicionales para reducir el uso de 'any'
interface FHIRMedicationResource extends FHIRResource {
  medicationCodeableConcept?: {
    text?: string;
  };
  dosageInstruction?: Array<{
    doseAndRate?: Array<{
      doseQuantity?: {
        value: number;
        unit: string;
      };
    }>;
    timing?: {
      text?: string;
    };
  }>;
  authoredOn?: string;
}

interface FHIREncounterResource extends FHIRResource {
  id: string;
  status?: string; // A√±adir la propiedad status que faltaba
  subject?: {
    reference: string;
  };
  participant?: Array<{
    individual?: {
      reference: string;
    };
  }>;
  period?: {
    start?: string;
  };
  reasonCode?: Array<{
    text?: string;
  }>;
  note?: Array<{
    text?: string;
  }>;
  serviceType?: {
    text?: string;
  };
  class?: {
    // A√±adir clase como opcional
    system: string;
    code: string;
    display: string;
  };
}

interface FHIRConditionResource extends FHIRResource {
  clinicalStatus?: {
    coding?: Array<{
      code?: string;
    }>;
  };
  code?: {
    text?: string;
  };
}

// Tipo para paciente FHIR
interface FHIRPatient extends FHIRResource {
  resourceType: 'Patient';
  name?: Array<{
    family?: string;
    given?: string[];
    use?: string;
    prefix?: string[];
    suffix?: string[];
  }>;
  gender?: string;
  birthDate?: string;
  telecom?: Array<{
    system?: string;
    value?: string;
    use?: string;
  }>;
  address?: Array<{
    line?: string[];
    city?: string;
    state?: string;
    postalCode?: string;
    country?: string;
    use?: string;
  }>;
  identifier?: Array<{
    system?: string;
    value?: string;
    type?: {
      coding?: Array<{
        system?: string;
        code?: string;
        display?: string;
      }>;
    };
  }>;
  active?: boolean;
}

// Tipo para bundle FHIR
interface FHIRBundle extends FHIRResource {
  resourceType: 'Bundle';
  type: string;
  total?: number;
  entry?: Array<{
    resource?: FHIRResource;
    fullUrl?: string;
    search?: {
      mode?: string;
      score?: number;
    };
  }>;
}

// Tipo para respuesta FHIR (usado en algunos m√©todos)
interface FHIRResponse {
  resourceType: string;
  id?: string;
  status?: string;
  location?: string;
  etag?: string;
  lastModified?: string;
}

// Tipo para datos adicionales que no se obtienen directamente del paciente
interface FHIRAdditionalData {
  allergies?: FHIRBundle;
  conditions?: FHIRBundle;
  observations?: FHIRBundle;
  medications?: FHIRBundle;
}

